import React, { useEffect, useState } from 'react'
import { useNavigate, useSearchParams } from 'react-router-dom';
import { API_URL } from '../services/apiService';

export default function Pixa() {
  // const [pixa_ar,setPixaAr] = useState([]);
  const [inp,setInp] = useState("");

  const [query] = useSearchParams();
  const queryS = query.get("s") || "monkeys";

  const nav = useNavigate();


  
  useEffect(() => {
    doApi();
    // query -> כל פעם שהיו אר אל משתנה הוא משתנה גם
    // ויזמן מחדש את הפונקציה
  },[/*/query*/])

  const doApi = async() => {
    // const queryQ = query.get("q") || "";

    // https://pixabay.com/api/?key=15489555-318fcca1200a48f374a1ce3ea&q=monkeys&image_type=photo&pretty=true
    // https://pixabay.com/api/?key=27132708-8c54ba3e2fa9897b06f49b52d&q=cats&image_type=photo&pretty=true
    const url = `https://pixabay.com/api/?key=15489555-318fcca1200a48f374a1ce3ea&q=]&image_type=photo&pretty=true`;
    try {
      const resp = await fetch(url);
      const data = await resp.json();

      setPixaAr(data);
    } catch (error) {
      console.log(error);
    }
  }

  const onSearch = () => {
    nav("/pixa?s="+inp)
  }

  return (
    <div className='container'>
      <h1>Pixa list:</h1>
      <div className='d-flex col-md-6 my-4'>
        <input onChange={(e) => setInp(e.currentTarget.value)} placeholder='Search for pixa...' type="search" className='form-control' />
        <button onClick={onSearch} className='btn btn-info'>Search</button>
      </div>
      <ul>
        {pixa_ar.map(item => {
          return (
            <li key={item._id}>
              {item.webformatURL}
            </li>
          )
        })}
      </ul>
    </div>
  )
}
