import React, { useState } from 'react';

export default function NextImg(props) {
    // סטייט ל-counter
    const [counter, setCounter] = useState(0);

    // נתיבי התמונות
    const images = [
        '../img/img1.jpeg',
        '../img/img2.jpeg',
        '../img/img3.jpeg',
        '../img/img4.jpeg'
    ];

    // פונקציה להוספת 1 ל-counter
    const onAdd1 = () => {
        setCounter((prevCounter) => (prevCounter + 1) % images.length);
    };

    // פונקציה להקטנת 1 מה-counter
    const onBack = () => {
        setCounter((prevCounter) => (prevCounter - 1 + images.length) % images.length);
    };

    return (
        <div>
            {/* הצגת התמונה */}
            <img src={images[counter]} alt={`Image ${counter + 1}`} style={{ width: '300px', height: '300px' }} />
            
            {/* הצגת המספר */}
            <h2>תמונה מספר {counter + 1}</h2>

            {/* כפתורי הניווט */}
            <button onClick={onBack}>Back</button>
            <button onClick={onAdd1}>Next</button>
        </div>
    );
}
