        <Link to="/games?year=2005">Games</Link> 
        {/* <Link to="/shop/clothing">Clothing</Link> |
        <Link to="/shop/animals">Animals</Link> */}
      </nav>
      <ul>
        {game_ar.map(item => {
          return (
            <li key={item.Game}>{item.Game} - {item.Year} </li>
          )
        })}
      </ul>
    </div>
  )
}





