import React, { useState } from 'react';

export default function CounterColor() {
    // יצירת סטייט עבור counter
    const [counter, setCounter] = useState(0);

    // מערך של צבעים שיתאים לערכים שונים של counter
    const colors = ['black', 'green', 'blue', 'yellow', 'orange'];

    // פונקציה להוספת 1 לערך counter
    const onAdd1 = () => {
        let newCounter = counter + 1;
        if (newCounter > 4) {
            newCounter = 0;
        }
        setCounter(newCounter);
    };

    // פונקציה להקטנת 1 מערך counter
    const onReduce1 = () => {
        let newCounter = counter - 1;
        if (newCounter < 0) {
            newCounter = 4;
        }
        setCounter(newCounter);
    };

    // הצגת הרכיב
    return (
        <div style={{ color: colors[counter] }}>
            <h2>Counter color: {counter}</h2>
            <button onClick={onReduce1}>Reduce 1</button>
            <button onClick={onAdd1}>Add 1</button>
        </div>
    );
}
